DELIMITER $$
CREATE TRIGGER `tr_autoIdGambar` BEFORE INSERT ON `gambar` FOR EACH ROW BEGIN
SET @hitung = CONVERT((RIGHT((SELECT idGambar FROM `gambar` ORDER by idGambar DESC LIMIT 1), 3)), UNSIGNED) + 1;
if (@hitung > 1) THEN
if (@hitung < 10) THEN 
SET new.idGambar = concat('G00',@hitung);
ELSEIF (@hitung < 100) THEN
SET new.idGambar = concat('G0',@hitung);
ELSE
SET new.idGambar = concat('G',@hitung);
END IF;
else 
SET new.idGambar = concat('G001');
END IF;
END
$$
